            text-align: center;
        }

        .last-updated {
            text-align: center;
            color: var(--text-muted);
            margin-bottom: 30px;
            font-size: 14px;
        }

        .section {
            margin-bottom: 25px;
            opacity: 0;
            animation: fadeIn 0.8s ease forwards;
        }

        .section:nth-child(1) { animation-delay: 1.1s; }
        .section:nth-child(2) { animation-delay: 1.2s; }
        .section:nth-child(3) { animation-delay: 1.3s; }
        .section:nth-child(4) { animation-delay: 1.4s; }
        .section:nth-child(5) { animation-delay: 1.5s; }
        .section:nth-child(6) { animation-delay: 1.6s; }
        .section:nth-child(7) { animation-delay: 1.7s; }
        .section:nth-child(8) { animation-delay: 1.8s; }

        @keyframes fadeIn {
            to {
                opacity: 1;
            }
        }

        .section-title {
            font-size: 20px;
            font-weight: 600;
            margin-bottom: 10px;
            color: var(--text-primary);
            display: flex;
            align-items: center;
        }

        .section-title::before {
            content: '';
            width: 4px;
            height: 20px;
            background: var(--color-primary);
            margin-right: 10px;
            border-radius: 2px;
        }

        .section-text {
            line-height: 1.6;
            color: var(--text-muted);
        }

        .section-text ul {
            margin: 10px 0;
            padding-left: 20px;
        }

        .section-text li {
            margin-bottom: 5px;
        }

        .section-text strong {
            color: var(--text-primary);
        }

        /* Buttons */
        .buttons-container {
            display: flex;
            gap: 20px;
            justify-content: center;
            margin-top: 40px;
            animation: fadeIn 1s ease 2s both;
        }

        .btn {
            padding: 15px 40px;
            border: none;
            border-radius: 50px;
            font-size: 16px;
            font-weight: 600;
            cursor: pointer;
            transition: all 0.3s ease;
            text-transform: uppercase;
            letter-spacing: 1px;
            position: relative;
            overflow: hidden;
        }

        .btn::before {
            content: '';
            position: absolute;
            top: 50%;
            left: 50%;
            width: 0;
            height: 0;
            border-radius: 50%;
            background: rgba(255, 255, 255, 0.1);
            transform: translate(-50%, -50%);
