        // Get URL parameters
        function getUrlParams() {
            const params = new URLSearchParams(window.location.search);
            return {
                lang: params.get('lang') || 'en',
                username: params.get('username') || 'User',
                user_id: params.get('user_id') || 'User'
            };
        }

        // Initialize page
        let currentLang = 'en';
        let currentUsername = 'User';
        let currentUserId = 'User';

        function initializePage() {
            const params = getUrlParams();
            currentLang = params.lang || 'en';
            currentUsername = params.username || 'User';
            currentUserId = params.user_id || 'User';

            // Create particles
            createParticles();

            // Load content
            loadContent();

            // Hide loading screen
            setTimeout(() => {
                document.getElementById('loadingScreen').classList.add('hidden');
            }, 2000);
        }

        // Create background diffused lights
        function createParticles() {
            const particlesContainer = document.getElementById('particles');
            const lightColors = [
                'rgba(230, 30, 133, 0.3)',
                'rgba(255, 64, 129, 0.25)',
                'rgba(156, 39, 176, 0.2)',
                'rgba(103, 58, 183, 0.15)',
                'rgba(63, 81, 181, 0.2)'
            ];

            const sizeClasses = ['small', 'medium', 'large'];

            for (let i = 0; i < 8; i++) {
                const light = document.createElement('div');
                const sizeClass = sizeClasses[Math.floor(Math.random() * sizeClasses.length)];
                light.className = `diffused-light ${sizeClass}`;
                light.style.background = `radial-gradient(circle, ${lightColors[i % lightColors.length]}, transparent)`;
                light.style.left = Math.random() * 100 + '%';
                light.style.top = Math.random() * 100 + '%';
                light.style.animationDelay = Math.random() * 20 + 's';
                light.style.animationDuration = (20 + Math.random() * 10) + 's';
                particlesContainer.appendChild(light);
            }
        }

        // Load content based on language
        function loadContent() {
            const t = translations[currentLang] || translations['en'];

            // Update welcome message
            const welcomeMessage = t.welcomeMessage.replace('${username}', currentUsername);
            document.getElementById('welcomeMessage').textContent = welcomeMessage;

            // Update title and date
            document.getElementById('termsTitle').textContent = t.termsTitle;
            document.getElementById('lastUpdated').textContent = t.lastUpdated;

            // Update buttons
            document.getElementById('acceptLabel').textContent = t.acceptLabel;
            document.getElementById('declineLabel').textContent = t.declineLabel;

            // Build terms content
            const termsContent = document.getElementById('termsContent');
            termsContent.innerHTML = `
                <div class="section">
                    <h3 class="section-title">${t.objectTitle}</h3>
                    <p class="section-text">${t.objectText}</p>
                </div>
                <div class="section">
                    <h3 class="section-title">${t.acceptanceTitle}</h3>
                    <p class="section-text">${t.acceptanceText}</p>
                </div>
                <div class="section">
                    <h3 class="section-title">${t.responsibleUseTitle}</h3>
                    <p class="section-text">${t.responsibleUseText}</p>
                </div>
                <div class="section">
                    <h3 class="section-title">${t.disclaimerTitle}</h3>
                    <p class="section-text">${t.disclaimerText}</p>
                </div>
                <div class="section">
                    <h3 class="section-title">${t.prohibitionsTitle}</h3>
                    <p class="section-text">${t.prohibitionsText}</p>
                </div>
                <div class="section">
                    <h3 class="section-title">${t.modificationsTitle}</h3>
                    <p class="section-text">${t.modificationsText}</p>
                </div>
                <div class="section">
                    <h3 class="section-title">${t.jurisdictionTitle}</h3>
                    <p class="section-text">${t.jurisdictionText}</p>
                </div>
                <div class="section">
                    <h3 class="section-title">${t.dataNoticeTitle}</h3>
                    <p class="section-text"><strong>${t.controllerTitle}</strong><br>${t.controllerText}</p>
                    <p class="section-text"><strong>${t.purposeTitle}</strong><br>${t.purposeText}</p>
                    <p class="section-text"><strong>${t.dataProcessedTitle}</strong><br>${t.dataProcessedText}</p>
                    <p class="section-text"><strong>${t.userRightsTitle}</strong><br>${t.userRightsText}</p>
                    <p class="section-text"><strong>${t.retentionTitle}</strong><br>${t.retentionText}</p>
                    <p class="section-text"><strong>${t.dataSharingTitle}</strong><br>${t.dataSharingText}</p>
                    <p class="section-text"><strong>${t.securityTitle}</strong><br>${t.securityText}</p>
                </div>
            `;
        }

        // Handle accept button
        async function handleAccept() {
            showSpinner();
            disableButtons();

            try {
                const response = await sendToWebhook(true);
                hideSpinner();
                showResponse(true, response);
            } catch (error) {
                hideSpinner();
                console.error('Error:', error);
                showResponse(true, null);
            }
        }

        // Handle decline button
        async function handleDecline() {
            showSpinner();
            disableButtons();

            try {
                const response = await sendToWebhook(false);
                hideSpinner();
                showResponse(false, response);
            } catch (error) {
                hideSpinner();
                console.error('Error:', error);
                showResponse(false, null);
            }
        }

        // Send data to webhook
        async function sendToWebhook(accepted) {
            const data = {
                username: currentUsername,
                lang: currentLang,
                accepted: accepted,
                timestamp: new Date().toISOString(),
                userAgent: navigator.userAgent,
                referrer: document.referrer
            };

            // Add user_id to query parameters
            const webhookUrl = `https://mumbai-sampling-conflicts-forest.trycloudflare.com/webhook/71a93ff3-2453-46c6-be4e-2865af10d1e5?user_id=${encodeURIComponent(currentUserId)}`;

            const response = await fetch(webhookUrl, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(data)
            });

            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }

            return await response.json();
        }

        // Show spinner
        function showSpinner() {
            document.getElementById('spinner').classList.add('show');
        }

        // Hide spinner
        function hideSpinner() {
            document.getElementById('spinner').classList.remove('show');
        }

        // Disable buttons
        function disableButtons() {
            document.getElementById('acceptBtn').disabled = true;
            document.getElementById('declineBtn').disabled = true;
        }

        // Enable buttons
        function enableButtons() {
            document.getElementById('acceptBtn').disabled = false;
            document.getElementById('declineBtn').disabled = false;
        }

        // Show response message
        function showResponse(accepted, serverResponse) {
            const t = translations[currentLang] || translations['en'];
            const responseDiv = document.getElementById('responseMessage');
            const titleElement = document.getElementById('responseTitle');
            const textElement = document.getElementById('responseText');

            if (serverResponse && serverResponse.length > 0 && serverResponse[0].body) {
                // Use webhook response
                const webhookData = serverResponse[0].body;
                titleElement.textContent = webhookData.username ? `¬°Gracias ${webhookData.username}!` : '¬°Gracias!';

                let responseMessage = '';
                if (webhookData.accepted) {
                    responseMessage = 'Tu aceptaci√≥n ha sido confirmada. Ahora tienes acceso completo a unimgai.';
                } else {
                    responseMessage = 'Entendemos tu decisi√≥n. Puedes aceptar los t√©rminos en cualquier momento cuando est√©s listo.';
                }

                textElement.innerHTML = responseMessage;
            } else {
                // Use default messages
                if (accepted) {
                    titleElement.textContent = t.acceptedTitle;
                    const message = t.acceptedMessage.replace('${username}', currentUsername);
                    textElement.innerHTML = message.replace(/\n/g, '<br>');
                } else {
                    titleElement.textContent = t.declinedTitle;
                    const message = t.declinedMessage.replace('${username}', currentUsername);
                    textElement.innerHTML = message.replace(/\n/g, '<br>');
                }
            }

            responseDiv.classList.add('show');
        }

        // Close app
        function closeApp() {
            // Try to close the window, if not possible, show a message
            if (window.close) {
                window.close();
            } else {
                // For mobile apps or when window.close() doesn't work
                document.body.innerHTML = `
                    <div style="display: flex; justify-content: center; align-items: center; height: 100vh; background: var(--bg-primary); color: var(--text-primary); text-align: center; padding: 20px;">
                        <div>
                            <h2 style="color: var(--color-primary); margin-bottom: 20px;">¬°Gracias por usar unimgai!</h2>
                            <p>Por favor, cierra esta ventana o regresa a Telegram.</p>
                        </div>
                    </div>
                `;
            }
        }

        // Initialize on page load
        document.addEventListener('DOMContentLoaded', initializePage);
    </script>
