        // Load Credits plans from webhook
        async function loadCreditsPlans() {
            const plansLoading = document.getElementById('plansLoading');
            const plansGrid = document.getElementById('plansGrid');
            const errorMessage = document.getElementById('errorMessage');

            // Show loading state
            if (plansLoading) plansLoading.style.display = 'block';
            if (plansGrid) plansGrid.style.display = 'none';
            if (errorMessage) errorMessage.style.display = 'none';

            console.log('üîó Intentando cargar planes desde webhook:', CREDITS_WEBHOOK_URL);

            try {
                // Intentar llamada GET al webhook con timeout
                const controller = new AbortController();
                const timeoutId = setTimeout(() => controller.abort(), 10000); // 10 segundos timeout

                const response = await fetch(CREDITS_WEBHOOK_URL, {
                    method: 'GET',
                    signal: controller.signal,
                    headers: {
                        'Accept': 'application/json'
                    }
                });

                clearTimeout(timeoutId);

                console.log('üì° Respuesta del webhook - Status:', response.status);

                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }

                const data = await response.json();
                console.log('‚úÖ Datos recibidos del webhook:', data);

                // El webhook puede devolver: array directo, objeto con 'plans', o array con objeto que tiene 'plans'
                let plansArray = null;

                if (Array.isArray(data)) {
                    // Si data es un array
                    if (data.length > 0 && data[0].plans && Array.isArray(data[0].plans)) {
                        // Array que contiene objeto con propiedad 'plans': [{ plans: [...] }]
                        plansArray = data[0].plans;
                    } else {
                        // Array directo de planes: [plan1, plan2, ...]
                        plansArray = data;
                    }
                } else if (data && data.plans && Array.isArray(data.plans)) {
                    // Objeto con propiedad 'plans': { plans: [...] }
                    plansArray = data.plans;
                }

                if (plansArray && plansArray.length > 0) {
                    console.log('‚úÖ Planes cargados exitosamente:', plansArray.length, 'planes');
                    displayCreditsPlans(plansArray);
                    return;
                } else {
                    throw new Error('Estructura de datos inv√°lida');
                }

            } catch (error) {
                console.warn('‚ö†Ô∏è No se pudo cargar desde el webhook:', error.message);
                console.log('üì¶ Usando datos de respaldo locales');

                // Usar datos de respaldo
                setTimeout(() => {
                    displayCreditsPlans(FALLBACK_PLANS_DATA);
                }, 500);
            }
        }

