                // Si el usuario seleccion√≥ una crypto espec√≠fica, la incluimos
                if (payCurrency) {
                    invoiceData.pay_currency = payCurrency;
                }

                console.log('Creating invoice with data:', invoiceData);

                const response = await fetch(`${apiUrl}/invoice`, {
                    method: 'POST',
                    headers: {
                        'x-api-key': NOWPAYMENTS_CONFIG.apiKey,
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify(invoiceData)
                });

                if (!response.ok) {
                    const errorData = await response.json();
                    console.error('Invoice creation failed:', errorData);
                    throw new Error(errorData.message || 'Failed to create invoice');
