// Script para n8n - Calcular costos de planes basado en sistema de cr√©ditos USD
// Los usuarios compran planes y reciben cr√©ditos (estrellas) para generar contenido

// Planes de compra de cr√©ditos - 1 USD = 100 cr√©ditos
const CREDIT_PLANS = [
  { 
    plan_price_usd: 5,
    credits_received: 500,
    plan_name: "Plan B√°sico"
  },
  { 
    plan_price_usd: 15, 
    credits_received: 1500,
    plan_name: "Plan Est√°ndar"
  },
  { 
    plan_price_usd: 25, 
    credits_received: 2500,
    plan_name: "Plan Premium"
  },
  { 
    plan_price_usd: 30, 
    credits_received: 3000,
    plan_name: "Plan Pro"
  }
];

// Costos de generaci√≥n en cr√©ditos
const GENERATION_COSTS = {
  image: 5,    // cr√©ditos por imagen
  video: 50    // cr√©ditos por video
};

// Obtener datos de entrada si existen
const inputData = $input.first()?.json;

// Variable para almacenar el resultado
let result;

if (inputData && inputData.plan_price) {
  // Calcular para un plan espec√≠fico por precio
  const plan = CREDIT_PLANS.find(p => p.plan_price_usd === inputData.plan_price);

  if (!plan) {
    result = {
      error: `No se encontr√≥ plan de $${inputData.plan_price} USD`,
      available_plans: CREDIT_PLANS.map(p => p.plan_price_usd),
      timestamp: new Date().toISOString(),
      symbol: 'USD',
      success: false
    };
  } else {
    const maxImages = Math.floor(plan.credits_received / GENERATION_COSTS.image);
    const maxVideos = Math.floor(plan.credits_received / GENERATION_COSTS.video);

    result = {
      symbol: 'USD',
      calculation_type: 'specific_plan',
      plan_price_requested: inputData.plan_price,
      timestamp: new Date().toISOString(),
      success: true,
      plan: {
        plan_name: plan.plan_name,
        plan_price_usd: plan.plan_price_usd,
        credits_received: plan.credits_received,
        generation_costs: GENERATION_COSTS,
        max_images_possible: maxImages,
        max_videos_possible: maxVideos,
        calculations: {
          credits_per_dollar: plan.credits_received / plan.plan_price_usd,
          images_calculation: `${plan.credits_received} cr√©ditos √∑ ${GENERATION_COSTS.image} = ${maxImages} im√°genes`,
          videos_calculation: `${plan.credits_received} cr√©ditos √∑ ${GENERATION_COSTS.video} = ${maxVideos} videos`
        }
      },
      summary: {
        symbol: 'USD',
        plan_name: plan.plan_name,
        plan_price_usd: plan.plan_price_usd,
        credits_received: plan.credits_received,
        max_images: maxImages,
        max_videos: maxVideos
      }
    };
  }
} else {
  // Calcular todos los planes disponibles
  const allPlans = [];

  for (const plan of CREDIT_PLANS) {
    const maxImages = Math.floor(plan.credits_received / GENERATION_COSTS.image);
    const maxVideos = Math.floor(plan.credits_received / GENERATION_COSTS.video);

    allPlans.push({
      plan_name: plan.plan_name,
      plan_price_usd: plan.plan_price_usd,
      credits_received: plan.credits_received,
      generation_costs: GENERATION_COSTS,
      max_images_possible: maxImages,
      max_videos_possible: maxVideos,
      calculations: {
        credits_per_dollar: plan.credits_received / plan.plan_price_usd,
        images_calculation: `${plan.credits_received} cr√©ditos √∑ ${GENERATION_COSTS.image} = ${maxImages} im√°genes`,
        videos_calculation: `${plan.credits_received} cr√©ditos √∑ ${GENERATION_COSTS.video} = ${maxVideos} videos`
      }
    });
  }

  const conversionRate = CREDIT_PLANS[0].credits_received / CREDIT_PLANS[0].plan_price_usd;

  result = {
    symbol: 'USD',
    calculation_type: 'all_plans',
    plans_info: {
      success: true,
      conversion_rate: conversionRate,
      conversion_description: `1 USD = ${conversionRate} cr√©ditos`,
      generation_costs: GENERATION_COSTS,
      total_plans: CREDIT_PLANS.length,
      timestamp: new Date().toISOString()
    },
    timestamp: new Date().toISOString(),
    success: true,
    plans: allPlans,
    summary: {
      symbol: 'USD',
      total_plans: allPlans.length,
      conversion_rate: conversionRate,
      conversion_description: `1 USD = ${conversionRate} cr√©ditos`,
      generation_costs: GENERATION_COSTS,
      available_plan_prices: CREDIT_PLANS.map(p => p.plan_price_usd)
    }
  };
}

// Retornar el resultado
return [{ json: result }];
